import React from 'react';
import Images from './common/Images';
import Area from './common/Area';
import Price from './common/Price';
import Address from './common/Address';
import './common.css';
import { connect } from 'react-redux';
import { isActiveAll } from '../Components/redux/actions/isActiveAllTemplate';
import { isActiveFirst } from '../Components/redux/actions/isActiveFirstTemplate';
import { isActiveSecond } from '../Components/redux/actions/isActiveSecondTemplate';

const Card = (
    {
        fullAddress, 
        area, 
        price, 
        images, 
        addTemplates,
        isActive}) => {

        const { 
            isActiveFirstTemplate,
            isActiveSecondTemplate,
            isActiveAllTemplate,
         } = isActive;

    const paint = (str, el, status) => {
        return str === "IMAGE" ? 
            <Images images={images} 
                pr={el.hasOwnProperty('children') ? 
                paint(el.children[0].component, el, true)  : null} 
                status={status}/> : str === "ADDRESS" ? 
                <Address fullAddress={fullAddress} 
                status={status}/> : str === "PRICE" ? 
                <Price price={price} status={status}/> : 
                str === "AREA" ? <Area area={area} status={status}/> : null;
    }
    return (
        <div>
            {isActiveFirstTemplate && 
                <div className='card'>
                {addTemplates.length !== 0 ?
                addTemplates[0].template.map(el =>paint(el.component, el , false))  
                 : <span>loading</span>}
                </div>
            }

            <div>
                <div className='card'>
                    {addTemplates.length !== 0 ?
                    addTemplates[1].template.map(el =>paint(el.component, el , false))  
                    : <span>loading</span>}
                </div>
            </div>
            <div>

            <div className='card'>
                    {addTemplates.length !== 0 ?
                    addTemplates[2].template.map(el =>paint(el.component, el , false))  
                    : <span>loading</span>}
                </div>
            </div>

        </div>
    );
};

const MSTP = (store) => ({
    isActive: store.isActive,
})

const MDTP = (dispatch) => ({
    isActiveAll:  () => {
        dispatch(isActiveAll())
    },
    isActiveFirst: () => {
        dispatch(isActiveFirst())
    },
    isActiveSecond : () => {
        dispatch(isActiveSecond())
    }
})

export default connect(MSTP, MDTP)(Card);
