import { call, put, all,takeEvery, } from 'redux-saga/effects';
import axios from "axios";
import {getItems} from '../actions/getItemsAction';
import {FETCH_REQUEST} from '../constants';

export function* fetchData() {
    try {
        const data = yield call(() => {
                return axios.get(`http://demo4452328.mockable.io/properties`)
                    .then(data => data.status === 200 ? data.data : [])
            }
        );
        // console.log(data.map(el => el.price));
        // console.log(data.data.map(el => el.price));
        const newData = data.data.map(el =>  ({

            id: el.id,
            full_address: el.full_address,
            area: el.area,
            price: el.price,
            images: el.images

        }))
        const resultDate = newData.reduce((acc, el) =>  acc.concat(el))


        console.log(resultDate);
        yield put(getItems(data.data));
    } catch (error) {
        yield put(error => console.log(error));
    }
}

export default function* watchFetch() {
    yield takeEvery(FETCH_REQUEST, fetchData);
}


