import * as a from '../constants';

const initialState = {
    'isActiveFirstTemplate': false,
    'isActiveSecondTemplate': false,
    'isActiveAllTemplate': false,
}

export default function getItemsReducer(state = initialState, action) {
    switch (action.type){
        case a.IS_ACTIVE_FIRST_TEMPLATE:
            return !state.isActiveFirstTemplate;

        case a.IS_ACTIVE_SECOND_TEMPLATE:
            return state.isActiveSecondTemplate;

        case a.IS_ACTIVE_ALL_TEMPLATE:
            return state.isActiveAllTemplate;

        default: return state;
    }
}
