import React from 'react';
import Images from './common/Images';
import Area from './common/Area';
import Price from './common/Price';
import Address from './common/Address';
import './common.css';
import { connect } from 'react-redux';
import {  } from '../Components/redux/actions/isActiveAllTemplate';
import {  } from '../Components/redux/actions/isActiveFirstTemplate';
import {  } from '../Components/redux/actions/'

const Card = ({fullAddress, area, price, images, addTemplates}) => {

    const paint = (str, el, status) => {
        return str === "IMAGE" ? 
            <Images images={images} 
                pr={el.hasOwnProperty('children') ? 
                paint(el.children[0].component, el, true)  : null} 
                status={status}/> : str === "ADDRESS" ? 
                <Address fullAddress={fullAddress} 
                status={status}/> : str === "PRICE" ? 
                <Price price={price} status={status}/> : 
                str === "AREA" ? <Area area={area} status={status}/> : null;
    }
    return (
        <div>
            <div>
                <div className='card'>
                {addTemplates.length !== 0 ?
                addTemplates[0].template.map(el =>paint(el.component, el , false))  
                 : <span>loading</span>}
                </div>
            </div>

            <div>
                <div className='card'>
                    {addTemplates.length !== 0 ?
                    addTemplates[1].template.map(el =>paint(el.component, el , false))  
                    : <span>loading</span>}
                </div>
            </div>
            <div>

            <div className='card'>
                    {addTemplates.length !== 0 ?
                    addTemplates[2].template.map(el =>paint(el.component, el , false))  
                    : <span>loading</span>}
                </div>
            </div>

        </div>
    );
};

const MSTP = (store) => ({
    isActive: store.isActive,
})

const MDTP = (dispatch) => ({
    fetchRequest:  () => {
        dispatch(fetchRequest())
    },
    getTemplatesRequest: () => {
        dispatch(getTemplatesRequest())
    }
})

export default connect(MSTP, MDTP)(Card);
